INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788991941885 ', '귀 기울여 들어 보니 큰스님 말씀 - 마음의 힘을 키워주는 55가지 이야기', '대행 (지은이), 김가을 (그림), 마음꽃을 피우는 사람들', '고래이야기', 16000, 'https://image.aladin.co.kr/product/28466/94/cover/8991941885_1.jpg', '힘들고 어려운 일을 겪거나 원하는 일이 있을 때 자신의 참마음인 주인공에게 어떻게 마음을 맡겨야 하는지, 그렇게 함으로써 어떤 결과를 얻을 수 있는지를 여러 예를 통해서 알려 준다. 한마디로 자신을 믿고 평온한 마음으로 살아가는 지혜를 알려 주는 책이다.', '224', '20211210');

