INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791186118610 ', '[큰글씨책] 우리가 사랑해야 하는 이유 - 최선의 관계를 찾아서', '앙투안 드 생텍쥐페리 (지은이), 송혜연 (옮긴이)', '생각속의집', 24000, 'https://image.aladin.co.kr/product/28392/59/cover/k532835816_1.jpg', '생텍쥐페리의 관계에 관한 주옥같은 글들을 모은 잠언집이다. 그는 수많은 작품을 통해서 관계의 소중함을 끊임없이 말해왔다. 이 책에서는 관계에서 행복을 발견한 생텍쥐페리의 길들임의 철학을 엿볼 수 있다.', '868', '20211210');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791186118627 ', '[큰글씨책] 좋은 기분 - 하루를 이기는 힘', '로버트 E. 세이어 (지은이), 김학영 (옮긴이)', '생각속의집', 27000, 'https://image.aladin.co.kr/product/28392/28/cover/k042835816_1.jpg', '저자는 이제 자신의 기분을 효과적으로 관리할 줄 아는 사람이 성공과 행복을 앞당길 수 있다고 말한다. 이런 의미에서 이 책은 번번이 자신의 기분 관리에 실패한 사람들에게 성공적인 기분 관리의 비밀을 명쾌하게 제시하고 있다.', '182', '20211210');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791187919933 ', '랑월 - 대전에서 살다가 골령골에 묻히다', '박현주 (지은이)', '모두의책', 20000, 'https://image.aladin.co.kr/product/28463/96/cover/k422835825_1.jpg', '일제강점기를 견뎌내고 민주주의 국가를 꿈꾸었던 사람들의 비극적이고 처절한 삶의 모습을 그려내고, 그들의 아름다운 꿈과 열정을 생생하게 표현하는 책. 전 지역에서 이름 없이 사라진 독립운동가들과 해방 후 건국운동가들의 삶을 소설로 복원하고 그들의 희생과 진실을 알리고 있다.', '814', '20211210');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791189486563 ', '글 씨를 받고', '안시찬 (지은이)', '오름(오름에디션)', 10000, 'https://image.aladin.co.kr/product/28532/92/cover/k842835036_1.jpg', '여기, 영적인 세계로 진입하는 한 생명이 있다. 시를 목숨처럼 끌고 다니며 한 몸이 되고자 열망하는 시인이 있다. 늦게 만나서 더욱 소중하고 애처로운 생명과 함께 슬프고도 아름다운 고개를 넘는 중이다. 시인의 얼굴에는 우수 같은 것이 서려 있다. 영혼의 세계, 잠재의식의 세계, 우주 자연과 가까워지면서 자연을 닮아가는 것일까.', '812', '20211210');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791190818131 ', '세상에서 제일 큰 숨은그림찾기 & 두뇌게임 - 종합편', '인나 아니키바 (지은이)', '시간과공간사', 12900, 'https://image.aladin.co.kr/product/28173/70/cover/k102835669_1.jpg', '가로 1m에 달하는 초대형 본문이 4겹으로 접혀 있는 ''신개념 폴더북'' 숨은그림찾기 책이다. 소제목이 적힌 커버 페이지를 양쪽으로 열면 총 4쪽에 달하는 거대한 세계가 열린다.', '691', '20211210');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
