INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791191447729 ', '오늘부터 행복한 사람이 되는 법을 가르쳐드립니다 :큰글자책 ', '연화민서 지음', '굿위즈덤', 25000, 'https://image.aladin.co.kr/product/28261/4/cover/k532835385_1.jpg', '저자는 행복해지기 위해 우선 나 자신을 마주보기 시작했다. 자신의 감정을 알아차리고 부정적인 감정에서 벗어나면서 성장했다. 그리고 지금은 나 자신을 싫어하지 않으려고, 행복하게 살아가려고 노력하고 있다. 일상의 일들과 심리치료를 하며 느꼈던 점을 함께 이 책에 담았다.', '199', '20211031');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
