INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788984816039 ', '관계를 치유하는 33가지 지혜 :성경 인물과 함께 떠나는 치유 여정 ', '글쓴이: 김영선', '생활성서(생활성서사)', 15000, 'https://image.aladin.co.kr/product/28142/43/cover/8984816035_1.jpg', '우리의 인생 과제인 관계 맺음, 그 어려운 일을 가장 현명하게 해 나가는 법을 성경의 지혜에서 찾아 주는, 저명한 성서학자 김영선 수녀의 「성경 인물과 함께 떠나는 치유 여정」 그 마지막 이야기. 몇 천 년의 지혜가 담긴 성경에는 오늘날 우리 마음도 가득 채울 수 있는 지혜와 통찰이 가득하다.', '233', '20211028');

