INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788962919349 ', '분쟁지명 동해, 현실과 기대 =East Sea, a disputed name ', '지은이: 주성재', '푸른길', 18000, 'https://image.aladin.co.kr/product/28121/99/cover/8962919346_1.jpg', '동해 명칭 확산 활동의 30년을 살펴보고 현재 상황과 전망을 정리한 책이다. 동해 명칭 확산 활동이 10년이 지나가는 시점부터 동해와 본격적인 인연을 맺은 저자는 동해를 국제사회에 알리는 활동을 통해 알게 된 동해의 역사, 동해와 관련된 이슈, 이에 대한 전망 등을 모두 담았다.', '988', '20211018');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788964214299 ', '(지속가능한 미래를 위한) 물 회복탄력성 ', '지은이: Johan Rockstr?m,박제량', '한티미디어', 27000, 'https://image.aladin.co.kr/product/28157/6/cover/8964214293_1.jpg', '수자원에 대한 새로운 회복탄력성 기반 접근 방식을 소개하며, 전 지구적 지속가능성을 위한 역할을 언급한다. 논의의 주제들은 예측 불가능한 변화에 대한 위험성; 전 지구적 물에 대한 인간의 영향과 의존성; 2050년까지 전 세계 인구의 식량 수요를 충족하기 위한 전망; 미래를 위한 경로이다.', '321', '20211018');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788964952283 ', '(수천억의 부를 가져오는) 감사의 힘 =Thank you power ', '샤넬 서 지음', 'BG북갤러리', 16500, 'https://image.aladin.co.kr/product/28136/0/cover/8964952286_2.jpg', '‘부자’가 되기 위한 상위 1%만의 비밀스러운 ‘땡큐 솔루션’을 제안하는 책이다. 엄청난 ‘부(富)’를 이루는 것과 ‘감사의 힘’과의 관계를 알기 쉽게 풀어낸 이 책은 ‘감사’를 통해 성공한 삶을 사는 이들의 감사 라이프스타일을 전하고 있으며, 성공을 꿈꾸는 직장인과 사회 리더계층 그리고 기업을 긍정적이고 창의적으로 이끌기를 원하는 CEO와 인사관리자들을 위한 시크릿 비법이 담겨있다.', '325', '20211018');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788967359546 ', '어른의 조건 :애매한 감정과 소박한 의문에 분명한 언어로 답하기 ', '지은이: 이시이 요지로,옮긴이: 최용우', '글항아리', 18000, 'https://image.aladin.co.kr/product/28159/56/cover/8967359543_1.jpg', '사람은 나이를 먹으면 자연히 이런 ‘어른’이 될 수 있을까? 생물학적 성인과 인간으로서 어른은 별개의 존재다. 자연히 될 수 없다면, 어른이 되기 위해 사람은 무엇을 배워야 할까. 이 특별한 한 권의 수업이 제공하는 것은 단련의 기회다.', '1', '20211018');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788984079700 ', '(유니콘 성장을 위한) 하이 그로스 핸드북 :1조 기업으로 성장시키는 스케일업 안내서 ', '일라드 길 지음 ;최기원 옮김', '세종(세종서적)', 21000, 'https://image.aladin.co.kr/product/28123/29/cover/8984079707_1.jpg', '유니콘 기업으로 성장할 수 있는 방법을 알려주는 스케일업 안내서로, 급격한 성장에 필요한 모든 전략을 담았다. 실리콘밸리에서 최고의 멘토로 일컫는 일라드 길이 2004년부터 각종 스타트업에 실무진 혹은 투자자로 참여하면서 경험한 여러 고성장 기업의 공통된 패턴과 그 해결책을 알려준다.', '325', '20211018');

