INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791191036701 ', '유튜버가 일본어 청취를 빠르게 익히는 방법 ', '저자: 최단시간일본어연구회', '수학연구사', 19500, 'https://image.aladin.co.kr/product/28145/11/cover/k972835764_1.jpg', '외국어 학습은 결국 효율의 싸움이다. 이번 책에서는 어학의 가장 최고봉인 리스닝 청취의 고차원적 고민에 대해서 다루도록 한다. 여기서의 고민들만 해결이 되면 여러분들의 리스닝 청취에는 문제가 없게 될 것이다.', '707', '20211015');

