INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791191211474 ', '마케터블 :디지털 시대를 리드하는 마케터의 DNA ', '진민규 지음', '북스톤', 15000, 'https://image.aladin.co.kr/product/28101/57/cover/k942734446_1.jpg', '디지털로 인한 변화, 새로움에 초점을 맞추다 보면 놓치기 쉬운 게 하나 있다. 변화하기 때문에 기준이 필요 없는 게 아니라, 변화를 바탕으로 새로운 기준이 필요하다는 사실 말이다. 《마케터블》은 디지털로 인해 무한증식하는 소비자와의 접점 속에서, ‘매출을 이끄는 마케터’는 어떻게 일하는지 이야기한다.', '326', '20211015');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791191278743 ', '케이프코드 ', '지은이: 헨리 데이비드 소로 ;옮긴이: 김병순', '교유당', 16800, 'https://image.aladin.co.kr/product/28095/53/cover/k982734342_1.jpg', '주로 내륙 지방의 숲이나 호수에 관한 책을 많이 남긴 소로가 바다에 대해 쓴 유일한 책이다. 소로는 케이프코드를 세 차례 찾아가 그곳에서 만난 자연 풍경과 바다, 사람들과 나눈 이야기들을 기록했다.', '846', '20211015');

