INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791191897050 ', '흰 빨래는 희게 빨고 검은 빨래 검게 빨아 :박순원 시집 ', '지은이: 박순원', '함께하는출판그룹파란', 10000, 'https://image.aladin.co.kr/product/28030/55/cover/k212734030_1.jpg', '파란시선 88권. 65년생 박순원의 인생극장을 펼쳐 보인다. 인생극장은 처음 읽으면 희극으로 보이지만 다시 읽으면 비극으로 상영된다. 시집을 넘기고 장면이 전환될 때마다 우리는 저항의 웃음과 함께, 버티기, 우기기, 비틀기의 삶과 시의 기술을 만나게 된다.', '812', '20211010');

