INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788991809864 ', '슬픔이 있는 곳에 기쁨을 ', '지은이: 이인복', '프란치스코 출판사', 12000, 'https://image.aladin.co.kr/product/28242/31/cover/8991809863_1.jpg', '저자 이인복 교수의 자전적 신앙 에세이다. 어린 시절 6·25로 가족의 이별과 죽음의 공포를 겪은 이야기로 시작한다. 학자로서 죽음에 관해 성찰하며 신앙인으로서 삶의 의미를 찾는다.', '234', '20211004');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788991809871 ', '고통이 있는 곳에 행복을 ', '지은이: 이인복', '프란치스코 출판사', 12000, 'https://image.aladin.co.kr/product/28242/33/cover/8991809871_1.jpg', '저자는 자신의 삶에서 고통스러웠던 때가 자신이 행복함을 알게 해주는 은총이라고 고백한다. 『고통이 있는 곳에 행복을』에는 그러한 은총의 이야기들로 채워져 있다. 이 책을 통해 우리는 좋으신 하느님께서 한 인간을 어떻게 이끄시고 섭리 하시는지 읽어낼 수 있다.', '230', '20211004');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788991809888 ', '하느님을 체험한 성경의 여인들 ', '이인복 지음', '프란치스코 출판사', 12000, 'https://image.aladin.co.kr/product/28242/32/cover/899180988x_1.jpg', '성서에 대한 정확한 이해를 바탕으로한 신학, 어머니와 아내의 삶에서 영글은 삶, 가톨릭 신자로서 지혜가 어우러졌다. 성경에 등장하는 여성들의 신앙 체험을 들여다보면서 저자가 체험한 신앙 체험을 연결하여 성경의 말씀이 살아있다는 것을 성찰한다.', '233', '20211004');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788991809895 ', '우리들 인생의 ''깔딱고개'' 이야기 ', '이인복 지음', '프란치스코출판사', 12000, 'https://image.aladin.co.kr/product/28242/35/cover/8991809898_1.jpg', '저자는 6·25에서 겪은 죽음의 참상과 가족의 이별, 행복하게 지내던 수도생활을 포기하고 가족을 부양해야 했던 10대 소녀 시절, 하례객 없이 소박하게 치른 혼인, 전 재산을 헌납하며 세운 나자렛성가회 법인의 우여곡절, 연로한 몸에서 오는 고통 등 삶의 모든 것을 문학으로 표현하였다.', '818', '20211004');

