INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791188471270 ', '미래에는 어떤 일을 해야 할까요? :미래직업에 대해 알아보아요 ', '오평선,옥윤성 지음', '윈타임즈', 16500, 'https://image.aladin.co.kr/product/27925/77/cover/k092734607_1.jpg', '사라질 직업보다는 새로 생겨날 직업에 대해 집중적으로 다루었다. 직업을 제대로 알아보고 신중히 결정할 수 있도록 미래에 대한 길라잡이 역할을 해준다. 청소년들이 직업에 관련한 체험을 하거나 책을 읽고, 추천 직업의 변동성과 가능성 등을 자세히 알아보는 기회를 줄 것이다.', '322', '20210913');

