INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791186972885 ', '(건축가와 함께 걷는) 고요와 평화로 지어올린 성당 ', '글·사진: 문마닐', 'J&jj(제이앤제이제이)', 17000, 'https://image.aladin.co.kr/product/27843/42/cover/k272734582_1.jpg', '문마닐 작가가 산속에 있는 수도원을 가기 위해 숙소를 이중으로 잡고, 현지인도 왜 가냐고 묻는 이름도 낯선 도시를 방문해서 관장과 대화하는가 하면 성당 하나를 위해 프랑스를 동서로 횡단하는 등 돈도 시간도 걸음도 아끼지 않고 성당을 보러 다닌 이야기다.', '982', '20210910');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791187038788 ', '온 세계가 마을로 온 날 :가장 어두울 때의 사랑에 관하여 ', '짐 디피디 지음 ;장상미 옮김', '갈라파고스', 15500, 'https://image.aladin.co.kr/product/27509/16/cover/k582734300_1.jpg', '2001년 9월 11일, 납치된 여객기들이 세계무역센터와 미국 국방부 청사에 충돌한다. 세상을 결코 사고 이전으로 돌아갈 수 없게 만든 참사의 순간, 미국 상공에는 4546대의 비행기가 운항 중이었다. 이 책은 하늘 위에서 갑자기 갈 곳을 잃은 사람들의 사고 이후 일주일을 들여다본다.', '848', '20210910');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791188510528 ', '고양이는 타인의 행복을 탐하지 않는다 :세계 자유고양이들이 전해 준 행복의 비밀 ', '글·사진: 이화자', '아라미', 15000, 'https://image.aladin.co.kr/product/27833/80/cover/k322734674_1.jpg', '세계 여행길에 마주친 길고양이들이 사는 법. 북촌의 한 골목길에서 떨고 있는 고양이를 만난 저자는 고양이와 묘한 동질감을 느낀다. 조그마한 발자국 소리에도 소스라치게 놀라 숨어 버리던 아기 고양이처럼 저자도 그렇게 세상이 두려웠기 때문이다.', '818', '20210910');

