INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791191133035 ', '외국어 학습담 :외국어 학습에 관한 언어 순례자 로버트 파우저의 경험과 생각 ', '로버트 파우저 지음', '혜화1117', 18500, 'https://image.aladin.co.kr/product/25967/86/cover/k352734584_1.jpg', '2018년 을 통해 세계 역사를 넘나들며 외국어가 전파되는 과정을 독자들에게 안내했던 로버트 파우저의 새 책. 모어인 영어는 물론 한국어, 일본어를 비롯 수많은 언어의 순례자로 널리 알려진 그가 외국어 학습을 어떻게 시작하고, 공부해왔는가에 대한 방법부터 한 번 배운 외국어 실력을 어떻게 유지할 수 있는가에 대한 길안내를 또다시 시작한다. 인공지능을 통해 실시간 통역과 번역이 가능해지는 이때, 우리에게 외국어를 배운다는 것은 어떤 의미인지까지 포괄하는 인문학적 외국어 학습법!', '707', '20210901');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
