INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791191169041 ', '지금이야, 무엇이든 괜찮아 ', '글·그림: 김정희', 'Kong', 15000, 'https://image.aladin.co.kr/product/27850/24/cover/k202734682_1.jpg', '유명하지 않은 작은 시골 마을에서 나고 자란 평범한 삶을 산 사람의 이야기다. 어려운 형편의 가정에서 농사짓는 부모님을 도우며 다섯 남매의 맏이로 자란 저자는 일찍 부모의 마음을 헤아려야 했지만 지나고 보니 어린 마음에 결핍이 되었다는 걸 알게 된다.', '818', '20210901');

