INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788965137160 ', '돈키호테는 수학 때문에 미쳤다 :큰글자책 ', '지은이: 김용관', '아름다운사람들', 25000, 'https://image.aladin.co.kr/product/27878/61/cover/8965137160_1.jpg', '독자들에게 익숙한 고전과 세계 명작 동화 그리고 근현대 소설을 소재로 우리가 미처 알지 못했던 수학의 세계로 초대한다. &lt;돈키호테는 수학 때문에 미쳤다&gt;의 저자는 광기의 다른 이름이라 할 수 있는 ‘수학’으로 돈키호테의 행동을 새롭게 분석했다.', '410', '20210831');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9788965403111 ', '돈 버는 최강 랜딩페이지 제작, 운용 시크릿 가이드 :P.D.L 3가지 패턴으로 떠나는 고객을 사로잡아라 ', '나카오 유타카 지음 ;안동현 옮김', '프리렉', 18000, 'https://image.aladin.co.kr/product/27861/88/cover/8965403111_2.jpg', '랜딩 페이지(Landing page) 즉, 랜딩을 목적으로 하는 페이지다. 고객은 검색이나 광고 등을 통해 가장 먼저 보게 되는 화면이고 머무르게 해야만 다음 단계로 이어질 수 있기에 가장 중요하고 핵심 가치를 표현한 페이지다. 홈페이지에 수많은 페이지를 만들고 매일 업데이트 하지만 매출은 그대로, 접속하는 사람들은 많지만 결과로 이어지지 않는 상황이라면 무엇이 문제일까.', '326', '20210831');

