INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
VALUES (BOOK_SEQ.NEXTVAL, '9791191840124 ', '진 달래 아리 :큰글씨책 ', '지은이: 윤성의', 'Yeondoo', 21000, 'https://image.aladin.co.kr/product/27846/99/cover/k182734586_1.jpg', '윤성의 에세이. 어느 결에 저자에게는 고양이와 여행 사이에 뗄레야 뗄 수 없는 연결고리가 생겨버렸다. 이제는 함께 살고 있는 고양이들을 보고 있으면 여행지에서 만났던 녀석들까지 덩달아 연상된다고 한다. 단숨에 여행의 조그마한 순간들까지 뻗어나가 생생한 추억으로 되살아나곤 하는 것이다.', '818', '20210831');

INSERT INTO TBL_BOOK (B_NO, B_ISBN, B_NAME, B_AUTHOR, B_PUBLISHER, B_PRICE, B_THUMBNAIL, B_INTRODUCE, B_KDC, B_PUBLISH_DATE) 
